mapper.select:
SELECT , sut.description as description, su.sub_user_name as subUserName, us.shop_name as shopName, user.payment_code_img as paymentCodeImg, user.user_name as userName, user.qq_number as QQNumber, sut.create_time as createTime FROM task_replace_order as tro JOIN sub_user_task as sut ON tro.task_id = sut.id JOIN sub_user as su ON sut.sub_user_id = su.id JOIN user_shop as us ON us.id = sut.shop_id JOIN user as user ON user.id = su.user_id
AND us.id in (select id from user_shop where user_id = #{pg.searchUserId})
AND tro.payment_status = #{pg.searchStatus}
AND ( su.sub_user_name LIKE concat(#{pg.searchName},'%') OR us.shop_name LIKE concat(#{pg.searchName},'%') OR tro.order_id LIKE concat(#{pg.searchName},'%') OR user.user_name LIKE concat(#{pg.searchName},'%') OR user.qq_number LIKE concat(#{pg.searchName},'%') )
AND (sut.create_time BETWEEN #{pg.searchStartDate} AND #{pg.searchEndDate})
AND sut.create_time >= #{pg.searchStartDate}
AND sut.create_time <= #{pg.searchEndDate}
